from collections import Counter
class Solution:
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: int
        """
        c = Counter(s)
        odd = []
        ans = 0
        for key, value in c.most_common():
            if value %2 == 0:
                ans += value
            else:
                odd.append((key, value))
        if odd :
            ans += odd[0][1]
            if len(odd) > 1 and odd[1][1] > 1:
                for key, value in odd[1:]:
                    if value > 1:
                        ans += value - 1
        return ans

